  useEffect(() => {
    const serviceItems = document.querySelectorAll<HTMLDivElement>(
      ".sevices-wrap .single-services"
    );

    function followImageCursor(event: MouseEvent, item: HTMLDivElement) {
      const contentBox = item.getBoundingClientRect();
      const dx = event.clientX - contentBox.x;
      const dy = event.clientY - contentBox.y;

      const imageElement = item.children[1] as HTMLElement | undefined;
      if (imageElement) {
        imageElement.style.transform = `translate(${dx}px, ${dy}px)`;
      }
    }

    const handlers: { item: HTMLDivElement; listener: (e: MouseEvent) => void }[] = [];

    serviceItems.forEach((item) => {
      const listener = (event: MouseEvent) => followImageCursor(event, item);
      item.addEventListener("mousemove", listener);
      handlers.push({ item, listener });
    });

    // Cleanup listeners on unmount
    return () => {
      handlers.forEach(({ item, listener }) => {
        item.removeEventListener("mousemove", listener);
      });
    };
  }, []);
